INSERT INTO `Book` VALUES (2, NULL, 'Brideshead Revisited', '9780316041560', 'tech', 100.00, 32.50, 'Waugh tells the story of the Marchmain family. Aristocratic, beautiful and charming, the Marchmains are indeed a symbol of England and her decline in this novel of the upper class of the 1920s and the abdication of responsibility in the 1930s.', 0, 'https://img1.doubanio.com/lpic/s4748479.jpg');
INSERT INTO `Book` VALUES (6, NULL, 'Twilight', '9780316043137', 'management', 100.00, 32.50, '\n\nEverything fans want to know about the hotly anticipated  Twilight  movie and much more!\n\n\n Designed as a celebration of the film, this lavishly illustrated paperback edition is an exclusive behind-the-scenes guide featuring full-color photos of the cast, locations, and sets, as well as storyboards, interviews, details of the special effects, and much more.', 0, 'https://img3.doubanio.com/lpic/s4265093.jpg');
